/* Le conteneur principal (carte) */
.control-card {
  width: 100%;
  max-width: 400px; /* Ajustable */
  background-color: #1e293b; /* slate-800 */
  padding: 1rem;
  border-radius: 0.75rem; /* xl */
  box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1);
  font-family: sans-serif;
  color: #e2e8f0;
}

/* Titre */
.control-title {
  font-size: 0.75rem; /* xs */
  color: #94a3b8; /* slate-400 */
  margin-bottom: 1rem;
  font-weight: 700;
  text-transform: uppercase;
  letter-spacing: 0.1em;
  text-align: center;
}

/* Légendes (Anti-horaire / Arrêt / Horaire) */
.labels {
  display: flex;
  justify-content: space-between;
  font-size: 10px;
  font-weight: 700;
  text-transform: uppercase;
  color: #64748b; /* slate-500 */
  margin-bottom: 0.25rem;
  padding: 0 0.25rem;
}

/* La piste du slider */
.slider-track {
  position: relative;
  height: 2.5rem; /* h-10 */
  background-color: #0f172a; /* slate-900 */
  border-radius: 9999px; /* full */
  border: 1px solid #334155; /* slate-700 */
  display: flex;
  align-items: center;
  overflow: hidden;
}

/* Marqueur central (Point Zéro) */
.center-marker {
  position: absolute;
  left: 50%;
  top: 0;
  bottom: 0;
  width: 2px;
  background-color: rgba(255, 255, 255, 0.2);
  z-index: 10;
}

/* La barre dynamique (couleur) */
.dynamic-bar {
  position: absolute;
  top: 0;
  bottom: 0;
  transition: all 0.1s ease;
  z-index: 5;
  /* Les styles left/right/color sont gérés par JS */
}

/* L'input range (invisible mais interactif) */
.range-input {
  position: absolute;
  inset: 0; /* top:0, right:0, bottom:0, left:0 */
  width: 100%;
  height: 100%;
  opacity: 0;
  cursor: e-resize; /* Curseur gauche-droite */
  z-index: 20;
  margin: 0;
}

/* Texte superposé (valeur) */
.value-display {
  width: 100%;
  text-align: center;
  font-size: 0.875rem; /* sm */
  font-family: monospace;
  font-weight: 700;
  z-index: 10;
  pointer-events: none; /* Laisse passer le clic vers l'input */
  /* Couleur gérée par JS */
}

