const speedInput = document.getElementById('speedInput');
const dynamicBar = document.getElementById('dynamicBar');
const valueText = document.getElementById('valueText');

function updateSlider() {
  const val = parseInt(speedInput.value);

  // 1. Calcul de la largeur (valeur absolue)
  // On divise par 2 car 100% de vitesse = 50% de la largeur totale du slider
  const widthPercentage = Math.abs(val) / 2;
  dynamicBar.style.width = widthPercentage + '%';

  // 2. Gestion Direction et Couleur
  if (val > 0) {
    // Vers la droite (Positif)
    dynamicBar.style.left = '50%';
    dynamicBar.style.right = 'auto';
    dynamicBar.style.backgroundColor = 'rgba(16, 185, 129, 0.5)'; // Emerald (Vert)

    valueText.textContent = '+' + val + '%';
    valueText.style.color = '#fff';
  } 
  else if (val < 0) {
    // Vers la gauche (Négatif)
    dynamicBar.style.left = 'auto';
    dynamicBar.style.right = '50%';
    dynamicBar.style.backgroundColor = 'rgba(239, 68, 68, 0.5)'; // Red (Rouge)

    valueText.textContent = val + '%';
    valueText.style.color = '#fff';
  } 
  else {
    // Zéro (Arrêt)
    dynamicBar.style.width = '0';

    valueText.textContent = 'HALT';
    valueText.style.color = '#64748b'; // Slate 500 (Gris)
  }
}

// Écouteur d'événement
speedInput.addEventListener('input', updateSlider);

// Initialisation
updateSlider();

