const gripInput = document.getElementById('gripInput');
const jawLeft = document.getElementById('jawLeft');
const jawRight = document.getElementById('jawRight');
const gripValue = document.getElementById('gripValue');

function updateGripper() {
  const value = parseInt(gripInput.value);
  const max = parseInt(gripInput.max);

  // Calcul du pourcentage d'ouverture (0% = ouvert, 100% = fermé)
  // Ou l'inverse selon votre logique mécanique. Ici : value 0 = ouvert.
  const percentage = (value / max) * 100;

  // Mise à jour du texte
  gripValue.textContent = value;

  // Calcul de la position CSS
  // calc(50% + Xpx) : On part du centre et on ajoute un décalage.
  // Plus le pourcentage est élevé (fermé), plus le décalage est petit (proche du centre).
  // 40px est l'écartement max, 4px l'écartement min (au contact).
  const offset = Math.max(4, 40 - (percentage / 100) * 40);

  // Appliquer les styles
  jawLeft.style.right = `calc(50% + ${offset}px)`;
  jawRight.style.left = `calc(50% + ${offset}px)`;

  // Changement de couleur dynamique (optionnel - devient plus rouge si très serré)
  if (percentage > 90) {
      jawLeft.style.backgroundColor = '#ef4444'; // rouge
      jawRight.style.backgroundColor = '#ef4444';
  } else {
      jawLeft.style.backgroundColor = '#06b6d4'; // cyan
      jawRight.style.backgroundColor = '#06b6d4';
  }
}

// Écouteur
gripInput.addEventListener('input', updateGripper);

// Init
updateGripper();

