js:
// Configuration
const SIZE = 200;
const CENTER = SIZE / 2;
const RADIUS = CENTER - 10; // 90

// Éléments DOM
const gaugeContainer = document.getElementById('gaugeContainer');
const rangeArc = document.getElementById('rangeArc');
const valueArc = document.getElementById('valueArc');
const minMarker = document.getElementById('minMarker');
const maxMarker = document.getElementById('maxMarker');
const valueText = document.getElementById('valueText');
const needleGroup = document.getElementById('needleGroup');
const inputMin = document.getElementById('gaugeMin');
const inputMax = document.getElementById('gaugeMax');
const inputValue = document.getElementById('gaugeValue');

let isDragging = false;

// --- FONCTIONS MATHÉMATIQUES ---

// Convertit polaire (angle/rayon) -> cartésien (x/y)
function polarToCartesian(centerX, centerY, radius, angleInDegrees) {
  // -90 pour commencer à midi (en haut)
  const angleInRadians = (angleInDegrees - 90) * Math.PI / 180.0;
  return {
    x: centerX + (radius * Math.cos(angleInRadians)),
    y: centerY + (radius * Math.sin(angleInRadians))
  };
}

// Crée la définition du chemin SVG (d) pour un arc
function describeArc(x, y, radius, startAngle, endAngle) {
    const start = polarToCartesian(x, y, radius, endAngle);
    const end = polarToCartesian(x, y, radius, startAngle);
    const largeArcFlag = endAngle - startAngle <= 180 ? "0" : "1";
    return [
        "M", start.x, start.y, 
        "A", radius, radius, 0, largeArcFlag, 0, end.x, end.y
    ].join(" ");
}

// --- LOGIQUE D'AFFICHAGE ---

function updateGaugeDisplay() {
    const min = parseInt(inputMin.value);
    const max = parseInt(inputMax.value);
    const val = parseInt(inputValue.value);

    // 1. Dessiner l'arc de plage (Min -> Max)
    rangeArc.setAttribute("d", describeArc(CENTER, CENTER, RADIUS, min, max));

    // 2. Dessiner l'arc de valeur (0 -> Val)
    // Note: On pourrait aussi faire (Min -> Val) selon le besoin
    valueArc.setAttribute("d", describeArc(CENTER, CENTER, RADIUS, 0, val));

    // 3. Positionner les marqueurs
    minMarker.setAttribute("transform", `rotate(${min} ${CENTER} ${CENTER})`);
    maxMarker.setAttribute("transform", `rotate(${max} ${CENTER} ${CENTER})`);

    // 4. Positionner l'aiguille
    needleGroup.setAttribute("transform", `rotate(${val} ${CENTER} ${CENTER})`);

    // 5. Mettre à jour le texte
    valueText.textContent = val + "°";
}

// --- LOGIQUE D'INTERACTION ---

function handleInteraction(e) {
    e.preventDefault(); // Empêche le scroll tactile
    const rect = gaugeContainer.getBoundingClientRect();

    // Support Souris & Touch
    const clientX = e.touches ? e.touches[0].clientX : e.clientX;
    const clientY = e.touches ? e.touches[0].clientY : e.clientY;

    const x = clientX - rect.left - CENTER;
    const y = clientY - rect.top - CENTER;

    // Calcul de l'angle (atan2 renvoie des radians entre -PI et PI)
    let angle = Math.atan2(y, x) * (180 / Math.PI);
    angle = angle + 90; // Réorienter : 0 en haut
    if (angle < 0) angle += 360; // Normaliser 0-360

    // Contraintes Min/Max
    const min = parseInt(inputMin.value);
    const max = parseInt(inputMax.value);

    // Logique "sticky" : ne saute pas directement de 359 à 0 si on est hors zone
    // Si on est dans la zone interdite, on colle à la borne la plus proche
    if (angle > max && angle < 360 - (360-max)/2) angle = max;
    if (angle < min && angle > min/2) angle = min;

    // Mise à jour si dans les limites
    if (angle >= min && angle <= max) {
        inputValue.value = Math.round(angle);
        updateGaugeDisplay();

        // Callback possible ici : console.log("Nouvel angle:", Math.round(angle));
    }
}

// Écouteurs d'événements
gaugeContainer.addEventListener('mousedown', (e) => {
    isDragging = true;
    handleInteraction(e);
});

window.addEventListener('mousemove', (e) => {
    if (isDragging) handleInteraction(e);
});

window.addEventListener('mouseup', () => {
    isDragging = false;
});

// Support Tactile
gaugeContainer.addEventListener('touchstart', (e) => {
    isDragging = true;
    handleInteraction(e);
}, { passive: false });

window.addEventListener('touchmove', (e) => {
    if (isDragging) handleInteraction(e);
}, { passive: false });

window.addEventListener('touchend', () => {
    isDragging = false;
});


// Initialisation
updateGaugeDisplay();

