// --- Éléments DOM ---
const btnConnect = document.getElementById('btnConnect');
const btnDownload = document.getElementById('btnDownload');
const statusText = document.getElementById('statusText');
const statusDot = document.getElementById('statusDot');
const consoleLog = document.getElementById('consoleLog');

// --- État ---
let port;
let writer;
let isConnected = false;

// Variables fictives pour l'exemple (normalement, elles viennent de vos jauges/sliders)
const servoData = {
    base: 90,
    shoulder: 45,
    elbow: 135,
    grip: 30
};

// --- Utilitaires ---
function log(msg) {
    const line = document.createElement('div');
    line.textContent = `> ${msg}`;
    consoleLog.appendChild(line);
    consoleLog.scrollTop = consoleLog.scrollHeight;
}

// --- 1. Web Serial API (Connexion) ---
async function connectSerial() {
    if (!("serial" in navigator)) {
        alert("Votre navigateur ne supporte pas le Web Serial API (Utilisez Chrome ou Edge).");
        return;
    }

    try {
        // Demande à l'utilisateur de choisir le port
        port = await navigator.serial.requestPort();
        await port.open({ baudRate: 9600 });

        // Configuration du Writer
        const textEncoder = new TextEncoderStream();
        const writableStreamClosed = textEncoder.readable.pipeTo(port.writable);
        writer = textEncoder.writable.getWriter();

        isConnected = true;
        updateUI(true);
        log("Connecté à l'Arduino !");

        // Lancement de la boucle d'envoi de données
        startDataLoop();

    } catch (err) {
        console.error(err);
        log("Erreur: " + err.message);
        updateUI(false);
    }
}

function updateUI(connected) {
    if (connected) {
        statusText.textContent = "EN LIGNE (9600)";
        statusText.style.color = "#10b981";
        statusDot.classList.add('connected');
        btnConnect.disabled = true;
        btnConnect.innerHTML = "<span>✅ Connecté</span>";
    } else {
        statusText.textContent = "DÉCONNECTÉ";
        statusText.style.color = "#cbd5e1";
        statusDot.classList.remove('connected');
        btnConnect.disabled = false;
        btnConnect.innerHTML = "<span>🔌 Connecter USB</span>";
    }
}

// Boucle d'envoi (Throttling à 50ms pour ne pas saturer l'Arduino)
async function startDataLoop() {
    while (isConnected && port.writable) {
        try {
            // Format CSV simple : "90,45,135,30\n"
            // Dans une vraie app, lisez ici les valeurs actuelles de vos inputs
            const dataString = `${servoData.base},${servoData.shoulder},${servoData.elbow},${servoData.grip}\n`;

            await writer.write(dataString);

            // Attendre 100ms
            await new Promise(r => setTimeout(r, 100));
        } catch (e) {
            log("Erreur d'écriture, déconnexion...");
            isConnected = false;
            updateUI(false);
            break;
        }
    }
}

// --- 2. Générateur de fichier .INO ---
function generateArduinoCode() {
    const code = `
/*
 * Code généré par ServoCore Studio
 * Réception Série CSV -> Servos
 */
#include <Servo.h>

// Configuration
Servo sBase, sShoulder, sElbow, sGrip;

void setup() {
  Serial.begin(9600);

  // Attachement des pins (A adapter)
  sBase.attach(3);
  sShoulder.attach(5);
  sElbow.attach(6);
  sGrip.attach(9);

  // Position initiale
  sBase.write(90);
  sShoulder.write(90);
}

void loop() {
  if (Serial.available() > 0) {
    // Lecture format: "val1,val2,val3,val4"
    int val1 = Serial.parseInt();
    int val2 = Serial.parseInt();
    int val3 = Serial.parseInt();
    int val4 = Serial.parseInt();

    if (Serial.read() == '\\n') {
      sBase.write(val1);
      sShoulder.write(val2);
      sElbow.write(val3);
      sGrip.write(val4);
    }
  }
}
    `;

    // Création du Blob et téléchargement
    const blob = new Blob([code], { type: 'text/x-c++src' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = "robot_controller.ino";
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);

    log("Fichier .INO généré !");
}

// --- Écouteurs ---
btnConnect.addEventListener('click', connectSerial);
btnDownload.addEventListener('click', generateArduinoCode);

css:
/* Conteneur de la scène */
.robot-scene {
  width: 100%;
  max-width: 500px;
  height: 400px; /* Espace pour bouger */
  background-color: #0f172a; /* slate-900 */
  border-radius: 1rem;
  border: 1px solid #334155;
  position: relative;
  overflow: hidden;
  display: flex;
  justify-content: center;
  align-items: flex-end; /* Le robot est posé au sol */
  font-family: sans-serif;
  user-select: none;
}

/* Grille de fond décorative */
.robot-scene::before {
  content: '';
  position: absolute;
  inset: 0;
  background-image: 
    linear-gradient(#1e293b 1px, transparent 1px),
    linear-gradient(90deg, #1e293b 1px, transparent 1px);
  background-size: 20px 20px;
  opacity: 0.3;
  pointer-events: none;
}

/* --- STRUCTURE DU BRAS --- */
/* Chaque partie est contenue dans la précédente pour hériter de la rotation */

/* 1. Base (Fixe au sol) */
.arm-base {
  width: 80px;
  height: 40px;
  background: #1e293b;
  border: 1px solid #475569;
  border-bottom: none;
  border-radius: 8px 8px 0 0;
  position: relative;
  z-index: 10;
  display: flex;
  justify-content: center;
  margin-bottom: 0; /* Collé au bas */
  box-shadow: 0 0 20px rgba(0,0,0,0.5);
}

/* Jointure Tournante (Tourelle) */
.joint-turret {
  width: 60px;
  height: 40px;
  background: #334155;
  border-radius: 50%;
  position: absolute;
  bottom: 20px; /* Relevé */
  display: flex;
  justify-content: center;
  align-items: center;
  /* Rotation Base */
  transform: rotate(var(--rot-base, 0deg)); 
  transition: transform 0.1s linear;
}

/* 2. Épaule (Premier segment long) */
.arm-upper {
  width: 20px;
  height: 120px;
  background: #0ea5e9; /* sky-500 */
  border: 1px solid #0284c7;
  border-radius: 10px;
  position: absolute;
  bottom: 20px; /* Ancre dans la tourelle */
  transform-origin: bottom center; /* PIVOT */
  transform: rotate(var(--rot-shoulder, 0deg));
  transition: transform 0.1s linear;
  box-shadow: inset 5px 0 10px rgba(0,0,0,0.2);
}

/* Déco Épaule */
.arm-upper::after {
  content: '';
  position: absolute;
  top: 10px; left: 5px; right: 5px; bottom: 10px;
  border: 1px dashed rgba(255,255,255,0.3);
  border-radius: 5px;
}

/* 3. Coude (Jointure + Avant-bras) */
.joint-elbow {
  width: 30px;
  height: 30px;
  background: #e2e8f0;
  border: 4px solid #334155;
  border-radius: 50%;
  position: absolute;
  top: -15px; /* Centré sur le haut du bras */
  left: 50%;
  transform: translateX(-50%);
  z-index: 2;
}

.arm-forearm {
  width: 16px;
  height: 90px;
  background: #06b6d4; /* cyan-500 */
  border: 1px solid #0891b2;
  border-radius: 8px;
  position: absolute;
  top: 15px; /* Part du centre du coude */
  left: 50%;
  transform: translateX(-50%) rotate(var(--rot-elbow, 0deg)); /* Rotation relative */
  transform-origin: top center; /* PIVOT */
  transition: transform 0.1s linear;
}

/* 4. Poignet et Pince */
.joint-wrist {
  width: 24px;
  height: 24px;
  background: #e2e8f0;
  border: 3px solid #334155;
  border-radius: 50%;
  position: absolute;
  bottom: -12px;
  left: 50%;
  transform: translateX(-50%);
}

.gripper-assembly {
  position: absolute;
  top: 10px; /* Sous le poignet */
  left: 50%;
  transform: translateX(-50%) rotate(var(--rot-wrist, 0deg));
  transform-origin: top center;
  transition: transform 0.1s linear;
  width: 40px;
  height: 40px;
}

/* Les doigts de la pince */
.claw {
  width: 6px;
  height: 25px;
  background: #ef4444; /* red-500 */
  position: absolute;
  top: 5px;
  transition: transform 0.1s;
}

.claw-left {
  left: 10px;
  border-radius: 0 0 0 10px;
  transform-origin: top right;
  transform: rotate(var(--grip-angle, 0deg)); /* Ouverture */
}

.claw-right {
  right: 10px;
  border-radius: 0 0 10px 0;
  transform-origin: top left;
  transform: rotate(calc(var(--grip-angle, 0deg) * -1)); /* Miroir */
}

/* --- Panneau de contrôle CSS --- */
.controls-panel {
  width: 100%;
  max-width: 500px;
  background: #1e293b;
  padding: 1rem;
  border-radius: 0.5rem;
  margin-top: 1rem;
  color: #e2e8f0;
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 1rem;
  font-size: 0.8rem;
}

.control-group {
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
}

.slider-label {
  display: flex;
  justify-content: space-between;
  font-weight: bold;
  color: #94a3b8;
}

input[type="range"] {
  width: 100%;
  height: 6px;
  background: #334155;
  border-radius: 3px;
  appearance: none;
  outline: none;
}

input[type="range"]::-webkit-slider-thumb {
  appearance: none;
  width: 16px;
  height: 16px;
  background: #22d3ee;
  border-radius: 50%;
  cursor: pointer;
  box-shadow: 0 0 10px rgba(34, 211, 238, 0.4);
}

