// Sélection des inputs
const inShoulder = document.getElementById('inShoulder');
const inElbow = document.getElementById('inElbow');
const inWrist = document.getElementById('inWrist');
const inGrip = document.getElementById('inGrip');

// Sélection des labels de valeur
const valShoulder = document.getElementById('valShoulder');
const valElbow = document.getElementById('valElbow');
const valWrist = document.getElementById('valWrist');
const valGrip = document.getElementById('valGrip');

// Sélection des éléments du bras (pour appliquer les styles)
const linkShoulder = document.getElementById('linkShoulder');
const linkForearm = document.getElementById('linkForearm');
const linkWrist = document.getElementById('linkWrist');
// La scène racine peut aussi stocker les variables si on veut une portée globale
const scene = document.getElementById('scene');

function updateArm() {
  // 1. Récupération des valeurs
  const vShoulder = inShoulder.value;
  const vElbow = inElbow.value;
  const vWrist = inWrist.value;
  const vGrip = inGrip.value;

  // 2. Mise à jour des textes
  valShoulder.textContent = `${vShoulder}°`;
  valElbow.textContent = `${vElbow}°`;
  valWrist.textContent = `${vWrist}°`;
  valGrip.textContent = vGrip > 40 ? "Ouverte" : (vGrip < 5 ? "Fermée" : `${Math.round((vGrip/45)*100)}%`);

  // 3. Application aux Variables CSS
  // On applique directement sur l'élément concerné pour la performance

  // Épaule : Rotation
  linkShoulder.style.setProperty('--rot-shoulder', `${vShoulder}deg`);

  // Coude : Rotation
  linkForearm.style.setProperty('--rot-elbow', `${vElbow}deg`);

  // Poignet : Rotation
  linkWrist.style.setProperty('--rot-wrist', `${vWrist}deg`);

  // Pince : Angle d'ouverture (appliqué à la scène ou au wrapper de la pince)
  // Comme les griffes sont enfants de linkWrist, on peut mettre la variable sur linkWrist
  linkWrist.style.setProperty('--grip-angle', `${vGrip}deg`);
}

// Écouteurs d'événements (Input pour temps réel, Change pour fin de glissement)
const inputs = [inShoulder, inElbow, inWrist, inGrip];
inputs.forEach(input => {
  input.addEventListener('input', updateArm);
});

// Initialisation au chargement
updateArm();

